/*
<figure class=img>
<a href=# class=gallery>
    <img src=./light-thumbs/thumb-57.jpg alt="Coraya baie mai 2017">
</a>
<figcaption itemprop="caption description">Égypte</figcaption>
<h3>Coraya baie mai 2017</h3>
</figure>
*/
const composeGallery = () => {
  const figure = Bouture.figure({class: 'img'})
  figure.a([{href: '#'}, {class: 'gallery'}])
    .img([{src: './light-thumbs/thumb-57.jpg', alt: 'Coraya baie mai 2017'}])
  figure.figcaption({itemprop: 'caption description'}, 'Égypte')
  figure.h3('Coraya baie mai 2017')

  return figure
}